        uint256 repayAmount
    ) external;

    function seize(
        address liquidator,
        address borrower,
        uint256 seizeTokens
    ) external;

    function liquidate(
        address borrower,
        uint256 repayAmount,
        IFixedLender fixedLenderCollateral,
        uint256 maturityDate
